import{_ as e,c as t,o,a}from"./app.a3fd5687.js";const u=JSON.parse('{"title":"\u8BBE\u5907\u8BA4\u8BC1","description":"","frontmatter":{},"headers":[{"level":2,"title":"\u8BA4\u8BC1\u65B9\u5F0F","slug":"\u8BA4\u8BC1\u65B9\u5F0F","link":"#\u8BA4\u8BC1\u65B9\u5F0F","children":[]},{"level":2,"title":"\u8FDE\u63A5\u72B6\u6001","slug":"\u8FDE\u63A5\u72B6\u6001","link":"#\u8FDE\u63A5\u72B6\u6001","children":[]}],"relativePath":"concepts/device/authentication.md"}'),c={name:"concepts/device/authentication.md"},s=a('<h1 id="\u8BBE\u5907\u8BA4\u8BC1" tabindex="-1">\u8BBE\u5907\u8BA4\u8BC1 <a class="header-anchor" href="#\u8BBE\u5907\u8BA4\u8BC1" aria-hidden="true">#</a></h1><p>\u5BF9\u4E8E\u63A5\u5165\u4E86 IoT \u5E73\u53F0\u7684\u8BBE\u5907\uFF0C\u5728\u5176\u53D1\u9001\u9065\u6D4B\u6570\u636E\uFF08Telemetry\uFF09\u6216\u8005\u63A5\u53D7\u5E73\u53F0\u4E0B\u53D1\u7684\u6307\u4EE4\uFF08Command and Control\uFF09\u4E4B\u524D\uFF0C\u5FC5\u987B\u5BF9\u5176\u8FDB\u884C\u8BA4\u8BC1\uFF0C\u8FD9\u4E2A\u8FC7\u7A0B\u975E\u5E38\u7C7B\u4F3C\u7528\u6237\u767B\u5F55\u3002</p><p>\u5728 <a href="./register.html">\u8BBE\u5907\u6CE8\u518C</a> \u4E00\u8282\u4E2D\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u5728 IoT \u5E73\u53F0\u6CE8\u518C\u4E86\u8BBE\u5907\u7684 ID \u548C\u5BC6\u94A5\uFF0C\u5F53\u8BBE\u5907\u542F\u52A8\u65F6\uFF0C\u53EF\u643A\u5E26\u8FD9\u4E24\u4E2A\u4FE1\u606F\u5230\u5E73\u53F0\u8BA4\u8BC1\u3002</p><h2 id="\u8BA4\u8BC1\u65B9\u5F0F" tabindex="-1">\u8BA4\u8BC1\u65B9\u5F0F <a class="header-anchor" href="#\u8BA4\u8BC1\u65B9\u5F0F" aria-hidden="true">#</a></h2><p>\u6700\u7B80\u5355\u7684\u8BA4\u8BC1\u5B9E\u73B0\u65B9\u5F0F\u5C31\u662F\u76F4\u63A5\u5728\u5E73\u53F0\u6570\u636E\u5E93\u91CC\u9762\u6821\u9A8C\u8FD9\u4E24\u4E2A\u4FE1\u606F\u662F\u5426\u5339\u914D\uFF0C\u8FD9\u4E5F\u662F\u5E38\u89C1\u7684\u7528\u6237\u767B\u5F55\u7684\u6821\u9A8C\u65B9\u6CD5\u3002\u8FD9\u79CD\u7B80\u5355\u7684\u6821\u9A8C\u65B9\u5F0F\u4F1A\u5728\u7F51\u7EDC\u4E0A\u4F20\u8F93\u660E\u6587\u5BC6\u94A5\u3002</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>\u5BF9\u4E8E\u53D1\u9001\u7528\u6237\u540D\u5BC6\u7801\u767B\u5F55\u7684\u65B9\u5F0F\uFF0C\u6211\u4EEC\u4E00\u822C\u4F1A\u5728\u5BA2\u6237\u7AEF\u5BF9\u5BC6\u7801\u52A0\u5BC6\u540E\u518D\u53D1\u9001\u7ED9\u670D\u52A1\u7AEF\u3002\u5982\u679C\u6211\u4EEC\u8981\u5BF9\u8BBE\u5907\u5BC6\u94A5\u52A0\u5BC6\uFF0C\u6211\u4EEC\u9700\u8981\u5F15\u5165\u53E6\u5916\u4E00\u7EC4\u516C\u79C1\u94A5\u3002</p></div><p>\u6211\u4EEC\u610F\u8BC6\u5230\uFF0C\u548C\u7528\u6237\u7684\u5BC6\u7801\u4E0D\u540C\uFF0C\u8BBE\u5907\u7684\u5BC6\u94A5\u672C\u8EAB\u5C31\u53EF\u4EE5\u7528\u6765\u52A0\u89E3\u5BC6\uFF0C\u6240\u4EE5\u6211\u4EEC\u53EF\u4EE5\u4F7F\u7528\u4E00\u79CD\u5178\u578B\u7684 code challenge \u8BA4\u8BC1\u65B9\u5F0F\u3002\u5176\u601D\u8DEF\u662F\u5728\u8BBE\u5907\u7AEF\u968F\u673A\u751F\u6210\u4E00\u6BB5\u5B57\u7B26\u4E32\uFF0C\u5BF9\u5176\u8FDB\u884C\u7B7E\u540D\uFF0C\u7136\u540E\u628A\u539F\u59CB\u5B57\u7B26\u4E32\u52A0\u4E0A\u7B7E\u540D\u4E00\u8D77\u53D1\u7ED9 IoT \u5E73\u53F0\uFF0C\u5E73\u53F0\u5BF9\u6570\u636E\u8FDB\u884C\u9A8C\u7B7E\uFF0C\u82E5\u5408\u6CD5\uFF0C\u5219\u8868\u793A\u8BA4\u8BC1\u901A\u8FC7\u3002</p><p>\u8FD9\u91CC\u7528\u5230\u4E86\u6570\u5B57\u7B7E\u540D\u91CC\u7684 <strong>\u7B7E\u540D</strong> \u548C <strong>\u9A8C\u7B7E</strong> \u601D\u60F3\uFF0C\u5404\u79CD\u8BED\u8A00\u90FD\u6709\u73B0\u6210\u7684\u5E93\u53EF\u4EE5\u76F4\u63A5\u4F7F\u7528\uFF0C\u4E00\u4E9B\u8BED\u8A00\u5982 Java \u8FD8\u63D0\u4F9B\u5185\u7F6E\u5B9E\u73B0\uFF0C\u65E0\u9700\u5F15\u5165\u7B2C\u4E09\u65B9\u5E93\u3002\u5176\u539F\u7406\u5E76\u4E0D\u590D\u6742\uFF0C\u4E0B\u9762\u7A0D\u5FAE\u5C55\u5F00\u8BA8\u8BBA\u4E00\u4E0B\u3002</p><p>\u5047\u8BBE\u53CC\u65B9\u5206\u522B\u6301\u6709\u516C\u94A5\u548C\u79C1\u94A5\uFF0C\u5728\u6211\u4EEC\u7684\u4F8B\u5B50\u4E2D\uFF0C\u8BBE\u5907\u7AEF\u6301\u6709\u79C1\u94A5\uFF0CIoT \u5E73\u53F0\u6301\u6709\u516C\u94A5\u3002</p><p><code>\u7B7E\u540D</code>\uFF1A\u6301\u6709\u79C1\u94A5\u7684\u4E00\u65B9\u9996\u5148\u4F7F\u7528\u6458\u8981\u7B97\u6CD5\u5982 SHA-256 \u8BA1\u7B97\u51FA\u6570\u636E\u7684\u6458\u8981\uFF0C\u7136\u540E\u7528\u79C1\u94A5\u5BF9\u6458\u8981\u8FDB\u884C\u52A0\u5BC6\uFF0C\u52A0\u5BC6\u540E\u7684\u6570\u636E\u53EB\u505A\u7B7E\u540D\uFF08signature\uFF09\u3002\u63A5\u4E0B\u6765\uFF0C\u79C1\u94A5\u6301\u6709\u65B9\u5C06\u539F\u59CB\u6570\u636E\u548C\u7B7E\u540D\u4E00\u8D77\u53D1\u51FA\u53BB\u3002</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>\u4E3A\u4EC0\u4E48\u8981\u5148\u8BA1\u7B97\u6458\u8981\u800C\u4E0D\u662F\u76F4\u63A5\u5BF9\u6570\u636E\u52A0\u5BC6\uFF1F\u56E0\u4E3A\u6570\u636E\u672C\u8EAB\u7684\u957F\u5EA6\u662F\u4E0D\u786E\u5B9A\u7684\uFF0C\u53EF\u4EE5\u4E3A\u4EFB\u610F\u957F\u5EA6\uFF0C\u4F46\u6458\u8981\u7684\u957F\u5EA6\u662F\u786E\u5B9A\u7684\uFF0CSHA-1 \u4E3A 20 \u5B57\u8282\uFF0CSHA-256 \u4E3A 32 \u5B57\u8282\u3002\u8FD9\u6837\u4E0D\u4EC5\u51CF\u5C11\u4E86\u52A0\u5BC6\u540E\u7684\u6570\u636E\u91CF\uFF0C\u8FD8\u51CF\u5C11\u4E86\u52A0\u5BC6\u65F6\u95F4\u3002</p></div><p><code>\u9A8C\u7B7E</code>\uFF1A\u6301\u6709\u516C\u94A5\u7684\u4E00\u65B9\u6536\u5230\u6570\u636E\u548C\u7B7E\u540D\u540E\uFF0C\u91C7\u7528\u540C\u6837\u7684\u6458\u8981\u7B97\u6CD5\u8BA1\u7B97\u51FA\u6570\u636E\u7684\u6458\u8981\uFF0C\u518D\u4F7F\u7528\u516C\u94A5\u5BF9\u7B7E\u540D\u8FDB\u884C\u89E3\u5BC6\uFF0C\u5C06\u7B2C\u4E00\u6B65\u548C\u7B2C\u4E8C\u6B65\u7684\u7ED3\u679C\u5BF9\u6BD4\uFF0C\u5982\u679C\u5339\u914D\u5219\u8868\u793A\u901A\u8FC7\u9A8C\u7B7E\uFF0C\u4E5F\u5C31\u662F\u8BF4\u6570\u636E\u786E\u5B9E\u662F\u79C1\u94A5\u6301\u6709\u8005\u53D1\u51FA\u7684\u3002</p><h2 id="\u8FDE\u63A5\u72B6\u6001" tabindex="-1">\u8FDE\u63A5\u72B6\u6001 <a class="header-anchor" href="#\u8FDE\u63A5\u72B6\u6001" aria-hidden="true">#</a></h2><p>IoT \u5E73\u53F0\u548C\u8BBE\u5907\u5BF9\u8BA4\u8BC1\u72B6\u6001\u7684\u5904\u7406\u53D6\u51B3\u4E8E\u8BBE\u5907\u548C\u5E73\u53F0\u7684\u8FDE\u63A5\u65B9\u5F0F\uFF0CIoT \u8BBE\u5907\u6709\u4E24\u79CD\u8FDE\u63A5\u65B9\u5F0F\uFF1A</p><ol><li>\u957F\u8FDE\u63A5</li></ol><p>\u5BF9\u4E8E\u9700\u8981\u4E91\u7AEF\u63A7\u5236\u8BBE\u5907\u7684\u573A\u666F\uFF0CIoT \u5E73\u53F0\u9700\u8981\u548C\u8BBE\u5907\u5EFA\u7ACB\u957F\u8FDE\u63A5\uFF0C\u8FD9\u6837\u624D\u80FD\u4FDD\u8BC1\u6570\u636E\u7684\u5B9E\u65F6\u6027\u3002\u8FD9\u79CD\u6A21\u5F0F\u4E0B\uFF0C\u5E94\u7528\u5C42\u901A\u8FC7 MQTT \u6216\u8005 AMQP \u7B49\u534F\u8BAE\u4F20\u8F93\u6570\u636E\u3002\u5728\u8FD9\u79CD\u8FDE\u63A5\u65B9\u5F0F\u4E0B\uFF0C\u6700\u7B80\u5355\u7684\u8BA4\u8BC1\u72B6\u6001\u5904\u7406\u5C31\u662F\u8BBE\u5907\u548C IoT \u4E91\u5EFA\u7ACB\u7684\u8FDE\u63A5\u672C\u8EAB\uFF0C\u4E5F\u5C31\u662F\u8BF4\u53EA\u6709\u8BA4\u8BC1\u901A\u8FC7\u7684\u8BBE\u5907\u624D\u80FD\u548C IoT \u4E91\u5EFA\u7ACB\u8FDE\u63A5\u3002</p><ol start="2"><li>\u65E0\u72B6\u6001\u8FDE\u63A5</li></ol><p>\u8FD9\u79CD\u65B9\u5F0F\u548C\u5178\u578B\u7684 Web \u5E94\u7528\u4E00\u81F4\uFF0C\u5E94\u7528\u5C42\u901A\u8FC7 HTTP\u3001CoAP \u7B49\u534F\u8BAE\u4F20\u8F93\u6570\u636E\u3002\u5F53\u8BBE\u5907\u901A\u8FC7\u8BA4\u8BC1\u540E\uFF0CIoT \u5E73\u53F0\u9700\u8981\u8FD4\u56DE\u67D0\u79CD\u51ED\u8BC1\uFF0C\u5E38\u89C1\u7684\u6709 SessionID\uFF08\u4F1A\u8BDD ID\uFF09\u548C JWT\uFF08\u5305\u542B\u8BA4\u8BC1\u4E3B\u4F53\u4FE1\u606F\u7684\u4EE4\u724C\uFF09\u3002\u8BBE\u5907\u7AEF\u9700\u8981\u7F13\u5B58\u51ED\u8BC1\u4EE5\u907F\u514D\u91CD\u590D\u8BA4\u8BC1\u3002</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>\u4F7F\u7528\u51ED\u8BC1\u7684\u65B9\u5F0F\u4F1A\u589E\u52A0\u6570\u636E\u4F20\u8F93\u91CF\uFF0C\u56E0\u4E3A\u540E\u7EED\u6BCF\u6B21\u53D1\u9001\u6570\u636E\u90FD\u8981\u643A\u5E26\u51ED\u8BC1\u3002</p></div>',19),i=[s];function l(p,n,r,d,h,_){return o(),t("div",null,i)}const m=e(c,[["render",l]]);export{u as __pageData,m as default};
